return items.map(item => {
  const videoLink = item.json["Video Link"];
  const match = videoLink.match(/(?:v=|\/)([0-9A-Za-z_-]{11})/);

  const videoId = match ? match[1] : null;

  return {
    json: {
      "Video Link": videoLink,
      "video_id": videoId
    }
  };
});

const data = items[0]?.json?.data?.transcripts;

if (!data || typeof data !== "object") {
  throw new Error("Transcript data not found in API response.");
}

// get first available language key
const firstLangKey = Object.keys(data)[0]; 
const transcriptChunks = data[firstLangKey]?.custom;

if (!Array.isArray(transcriptChunks)) {
  throw new Error("Transcript chunks not found or not in expected format.");
}

let combinedText = transcriptChunks
  .map(chunk => chunk?.text?.trim())
  .filter(Boolean)
  .join(" ");

return [
  {
    json: {
      combinedTranscript: combinedText
    }
  }
];

