// This script takes a raw string input and formats it into a JSON array of titles,
// preserving the numbering and bold formatting as shown in the desired output.

// Get the full text output from the previous node.
const inputString = $input.first().json.output;

// 1. Isolate the list of titles by finding the position after the introductory text.
// This is a robust method to handle different introductory paragraphs.
const titlesStartMarker = "youtube titles for your video:\\n\\n";
let titlesSection = "";

const titlesStartIndex = inputString.indexOf(titlesStartMarker);
if (titlesStartIndex !== -1) {
    titlesSection = inputString.substring(titlesStartIndex + titlesStartMarker.length);
} else {
    // Fallback: If the marker is not found, assume the entire input is the list.
    titlesSection = inputString;
}

// 2. Split the section into individual titles using the escaped newline character, '\\n', as a separator.
const rawTitles = titlesSection.split('\\n');

// Filter out any empty strings and ensure we only process the first 10 titles, as is the standard for this type of input.
const listItems = rawTitles.filter(line => line.trim().length > 0).slice(0, 10);

const formattedTitlesArray = [];

for (const line of listItems) {
    // 3. Keep the line as-is, ensuring it's properly trimmed.
    let formattedTitle = line.trim();

    // The raw string from the AI output might contain trailing quotes from the JSON string.
    // We remove them if they exist to keep the titles clean.
    formattedTitle = formattedTitle.replace(/\s*,\s*"|"\s*$/g, '');

    // Add the formatted title to the final array.
    formattedTitlesArray.push(formattedTitle);
}

// 4. Return a single output item that contains the final desired JSON structure,
//    which is an array of the formatted titles.
return [{
    titles: formattedTitlesArray
}];
